select f.st_name as name, (f.feb7d - j.jan7d) as diff_7d, (f.feb30d - j.jan30d) as diff_30d from (select name as st_name, sum(7d) as jan7d, sum(30d) as jan30d from fares_jan18 inner join stations on fares_jan18.station = stations.name where line = 'Broadway' group by station) j join (select name as st_name, sum(7d) as feb7d, sum(30d) as feb30d from fares_feb1 join stations on fares_feb1.station = stations.name where line = 'Broadway' group by station) f on j.st_name = f.st_name;
